DROP TABLE IF EXISTS `hangbanxinxi`;
CREATE TABLE `hangbanxinxi` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `hangbanbianhao` varchar(50) NOT NULL DEFAULT '' COMMENT '航班编号',
  `hangbanmingcheng` varchar(255) NOT NULL DEFAULT '' COMMENT '航班名称',
  `leixing` varchar(50) NOT NULL DEFAULT '' COMMENT '类型',
  `shifachengshi` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '始发城市',
  `zhongdaochengshi` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '终到城市',
  `qifeishijian` varchar(25) NOT NULL DEFAULT '' COMMENT '起飞时间',
  `cangweileixing` varchar(50) NOT NULL DEFAULT '' COMMENT '仓位类型',
  `piaojia` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '票价',
  `shengyupiaoshu` int(11) NOT NULL DEFAULT '0' COMMENT '剩余票数',
  `hangbanjieshao` longtext NOT NULL COMMENT '航班介绍',
  PRIMARY KEY (`id`),
  KEY `hangbanxinxi_shifachengshi_index` (`shifachengshi`),
  KEY `hangbanxinxi_zhongdaochengshi_index` (`zhongdaochengshi`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8mb4 COMMENT='航班信息';

