  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  PRIMARY KEY (`id`),
  KEY `jiudianfangjian_jiudianxinxiid_index` (`jiudianxinxiid`),
