-- ----------------------------
-- Table structure for `jiudianxinxi`
-- ----------------------------
DROP TABLE IF EXISTS `jiudianxinxi`;
CREATE TABLE `jiudianxinxi` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `jiudianbianhao` varchar(50) NOT NULL DEFAULT '' COMMENT '酒店编号',
  `jiudianmingcheng` varchar(255) NOT NULL DEFAULT '' COMMENT '酒店名称',
  `suoshudiqu` varchar(50) NOT NULL DEFAULT '' COMMENT '所属地区',
  `jiudiantupian` text NOT NULL COMMENT '酒店图片',
  `jiudiandizhi` varchar(255) NOT NULL DEFAULT '' COMMENT '酒店地址',
  `tianjiaren` varchar(64) NOT NULL DEFAULT '' COMMENT '添加人',
  `jiudianxiangqing` longtext NOT NULL COMMENT '酒店详情',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8mb4 COMMENT='酒店信息';

-- ----------------------------
-- Records of jiudianxinxi
-- ----------------------------
INSERT INTO `jiudianxinxi` VALUES ('1', '2101041537120', '深圳宝安体育馆希尔顿欢朋酒店', '北京', 'upload/f72ed15d08e04d3983b37b259fd27bc6.png,upload/1735628041805.png', 'xxx地址', '', '<p>深圳宝安体育馆希尔顿欢朋酒店是一家国际中高端商务连锁酒店地处宝安区新湖路4008号，位于深圳市宝安区商业中心，紧临107国道，广深高速宝安出口，距深圳机场、深圳北站、深圳新国际会展中心车程较短，交通极为便利。酒店设有各式温馨舒适客房201间，独具希尔顿欢朋特色的“HUB”大堂，集欢迎区、聚会区、休闲区和商务区于一体。多间舒适客房使用美国“Hampton Bed”标准的舒达双专利床垫和生态认证床上用品，并采用希尔顿专用“Peter Thomas Roth”卫浴用品。集营养健康热早餐的欢朋餐厅，乐动健身房，可容纳180人精英会议室和恰到好处的知己服务，使客人享受到高品质的快乐入住体验。无论您是繁忙的商务人士，抑或是休闲的度假游客，这里都将是您的优质之选</p>');
INSERT INTO `jiudianxinxi` VALUES ('2', '2101041544572', '深圳南山亚朵S酒店', '北京', 'upload/f48c059943bf456bb88be8173fbe8914.png,upload/1735628035417.png', 'xxx地址', '', '<p>酒店地处南山区北环大道豪方天际广场超甲级写字楼大厦—南山区科技园核心圈，毗邻前海商圈、距宝安中心区一步之遥。靠近花卉小镇，深圳湾，中洲购物广场，海雅缤纷城，壹方城购物中心，海岸城等商圈。<br/>酒店是亚朵酒店的升级品牌，“S”为“Supre”的英文字母缩写，专为商旅人士而设计从环境设施，客房用品等全方位做了升级，提供以富有格调的设计，舒适的人文环境和温暖服务。</p>');
INSERT INTO `jiudianxinxi` VALUES ('3', '2101041547030', '深圳滨河时代ICON LAB艾垦酒店', '柳州', 'upload/ae362293a357010c80047483e0203e33.png,upload/1735628024952.png', 'xxx地址', '', '<p>酒店坐落于深圳市福田区CBD中心，酒店邻近KKONE时尚购物商场及CT Mall，集购物、餐饮、娱乐等为一体的大型综合商业体，酒店周边就是高档商业大楼集中区，NEO大楼、招商大厦、深业泰然大厦、海松大厦等写字楼，距离深圳国际会展中心约8分钟车程，酒店邻近地铁7号线，步行约3分钟。<br/>酒店是深圳市兰兹酒店管理有限公司旗下的高端酒店品牌，其设计理念是以追求符合当代城市人的美学生活方式为主题。在天际房，蓄上温热的40度，让您沉浸在窗边浴缸里清心静体，举杯邀明月，坐看云起时。</p>');
INSERT INTO `jiudianxinxi` VALUES ('4', '2101041548150', '深圳云著酒店', '哈尔滨', 'upload/7de72200e7baec23738651f6bc29c209.png,upload/1735628017721.png', 'xxx地址', '', '<p>深圳市云著酒店地处坂田商业中心区佳华领汇广场，酒店主体高23层，总建筑面积约1.3万平米。<br/>酒店与Max City购物商场相结合，是一个集购物、娱乐、餐饮、住宿为一体，致力打造一站式住宿体验。酒店毗邻华为总部基地、天安云谷、星河WORLD.COCOPark、宝能科技园、富士康、金荣达工业园、硅谷动力清湖园总部等大型高端科技商业圈。距离地铁5号线五和站C/D出口仅约800米，交通便利快捷。<br/>酒店设有180余间客房，壹食餐厅、大堂吧、健身房、自助洗衣房、4个不同规模的会议厅以及722平方米的空中花园等配套设施，同时拥有专业会议服务团队，是您商务活动的优质首选。壹食餐厅位于酒店八楼，全场落地玻璃俯瞰外景，与七楼的空中花园无缝衔接。环境别致、优雅、浪漫，为宾客提供了一个完美用餐空间。餐厅秉持绿色、健康、美味的烹饪理念，选自来自世界各地的新鲜食材，打造星级早餐、贴心商务午餐以及东南亚特色菜系晚餐菜谱，为您提供丰富美味的全天候餐饮服务。酒店商场里设有电影院、大型超市、购物区、休闲区以及琳琅满目的各地风味人气美食，为您提供一个人自在放松的空间，在这里能够休憩、充电、放松，是您商务出行及旅游的选择。</p>');

