  `liuyanren` varchar(64) NOT NULL DEFAULT '' COMMENT '留言人',
  `huifuneirong` text NOT NULL COMMENT '回复内容',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COMMENT='留言板';

