-- ----------------------------
-- Table structure for `quxiaodingpiao`
-- ----------------------------
DROP TABLE IF EXISTS `quxiaodingpiao`;
CREATE TABLE `quxiaodingpiao` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `dingpiaoxinxiid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订票信息id',
  `hangbanbianhao` varchar(50) NOT NULL DEFAULT '' COMMENT '航班编号',
  `hangbanmingcheng` varchar(255) NOT NULL DEFAULT '' COMMENT '航班名称',
  `leixing` varchar(50) NOT NULL DEFAULT '' COMMENT '类型',
  `shifachengshi` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '始发城市',
  `zhongdaochengshi` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '终到城市',
  `cangweileixing` varchar(50) NOT NULL DEFAULT '' COMMENT '仓位类型',
  `dingpiaoshu` int(11) NOT NULL DEFAULT '0' COMMENT '订票数',
  `dingpiaozonge` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '订票总额',
  `xingming` varchar(50) NOT NULL DEFAULT '' COMMENT '姓名',
  `dingpiaoyonghu` varchar(64) NOT NULL DEFAULT '' COMMENT '订票用户',
  `quxiaoyuanyin` text NOT NULL COMMENT '取消原因',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '取消时间',
  PRIMARY KEY (`id`),
  KEY `quxiaodingpiao_dingpiaoxinxiid_index` (`dingpiaoxinxiid`),
  KEY `quxiaodingpiao_shifachengshi_index` (`shifachengshi`),
  KEY `quxiaodingpiao_zhongdaochengshi_index` (`zhongdaochengshi`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COMMENT='取消订票';

