-- ----------------------------
-- Table structure for `quxiaoyuding`
-- ----------------------------
DROP TABLE IF EXISTS `quxiaoyuding`;
CREATE TABLE `quxiaoyuding` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `fangjianyudingid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '房间预订id',
  `jiudianbianhao` varchar(50) NOT NULL DEFAULT '' COMMENT '酒店编号',
  `jiudianmingcheng` varchar(255) NOT NULL DEFAULT '' COMMENT '酒店名称',
  `suoshudiqu` varchar(50) NOT NULL DEFAULT '' COMMENT '所属地区',
  `leixing` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '类型',
  `fangjiantupian` varchar(255) NOT NULL DEFAULT '' COMMENT '房间图片',
  `jiage` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '价格',
  `yudingbianhao` varchar(50) NOT NULL DEFAULT '' COMMENT '预定编号',
  `yujiruzhuriqi` varchar(25) NOT NULL DEFAULT '' COMMENT '预计入住日期',
  `yudingtianshu` int(11) NOT NULL DEFAULT '0' COMMENT '预定天数',
  `xingming` varchar(50) NOT NULL DEFAULT '' COMMENT '姓名',
  `yudingjine` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '预定金额',
  `yudingren` varchar(64) NOT NULL DEFAULT '' COMMENT '预订人',
  `quxiaoshuoming` text NOT NULL COMMENT '取消说明',
  `addtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '取消时间',
  PRIMARY KEY (`id`),
  KEY `quxiaoyuding_fangjianyudingid_index` (`fangjianyudingid`),
  KEY `quxiaoyuding_leixing_index` (`leixing`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8mb4 COMMENT='取消预订';

